import { useState } from 'react'
import React from 'react';
import { Button, Typography } from '@material-ui/core';
import useStyles from './productFormStyles';

function ProductForm() {
    const classes = useStyles()

    const [name, setName] = useState("")
    const [imgURL, setImgURL] = useState("")
    const [price, setPrice] = useState("")
    const [details, setDetails] = useState("")


    function handleName(e){
        console.log(e.target.value)
        setName(e.target.value)
    }

    function handleImgURL(e){
        console.log(e.target.value)
        setImgURL(e.target.value)
    }

    function handlePrice(e){
        console.log(e.target.value)

        setPrice(`$${e.target.value}`)
    }

    function handleDetails(e){
        console.log(e.target.value)
        setDetails(e.target.value)
    }


    return (
        <div>
            <h1> Become a seller?</h1>
            <form>
            <input type="text" placeholder="Shoe Name" onChange={handleName}/><br/>
                <input type="text" placeholder="ImgURL" onChange={handleImgURL}/> <br/>
                <input type="text" placeholder="price" onChange={handlePrice}/><br/>
                <input type="text" placeholder="details" onChange={handleDetails}/><br/>

                <input type="submit" value="Publish"/>
            </form>

            <Typography>
                    <strong>{name}</strong>
            </Typography>

            <Typography>
            <img src={imgURL}  height="40px"  />
            </Typography>

            <Typography>
                    <strong>{price}</strong>
            </Typography>

            <Typography>
                    <strong>{details}</strong>
            </Typography>

            <div>
            <Card className={classes.root}>

                <CardMedia className={classes.media} image={product.image} title={product.name} />

                <CardContent>
                    <div className={classes.cardContent}>
                        <Typography variant="h5" gutterBottom>
                            {product.name}
                        </Typography>
                        <Typography variant="h6">
                            {product.price}
                        </Typography>
                    </div>
                    <Typography variant="overline" color="textSecondary">
                        {product.description}
                    </Typography>
                </CardContent>

                <CardActions disableSpacing className={classes.cardActions}>
                    {!isLiked ? <FavoriteBorder onClick={handleLike}/> : <Favorite onClick={handleLike}/>} 
                    <IconButton aria-label="Add to Cart" onClick={handleClick}>
                        <AddShoppingCart />
                    </IconButton>
                    <Typography variant="overline" color="textSecondary">
                    {likeNumbers} Favorites
                    </Typography>
                </CardActions>

            </Card>
        </div>

        </div>
    )

}

export default ProductForm;
