import { useState } from 'react'
import React from 'react';
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton, Grid } from '@material-ui/core';
import useStyles from './ProductFormStyles';
import { AddShoppingCart, Favorite, FavoriteBorder } from "@material-ui/icons";



function ProductForm() {
    const classes = useStyles()

    const src = 'https://static.nike.com/a/images/t_PDP_1728_v1/f_auto,b_rgb:f5f5f5/2d9af5a0-de12-4fc1-ac5c-43a085a23cb1/react-vision-womens-shoes-kGhsR6.png'


    const [name, setName] = useState("")
    const [imgURL, setImgURL] = useState("")
    const [price, setPrice] = useState("")
    const [details, setDetails] = useState("")

    const [formData, setFormData] = useState({
        name: "",
        image: "",
        quantity: 0,
        price: "",
        description: "SKU: 100",
        details: "",
        likes : 0
    })

    function handleFormData(e){
        const name = e.target.name
        const value = e.target.value

        console.log(name)
        console.log(value)

    }


    function handleName(e){
        console.log(e.target.value)
        setName(e.target.value)
    }

    function handleImgURL(e){
        console.log(e.target.value)
        setImgURL(e.target.value)
    }

    function handlePrice(e){
        console.log(e.target.value)

        setPrice(`$${e.target.value}`)
    }

    function handleDetails(e){
        console.log(e.target.value)
        setDetails(e.target.value)
    }


    return (
        <div>
            <h1> Become a seller?</h1>
            <form>
            <input type="text" placeholder="Shoe Name" onChange={handleFormData} value = {name} name = "name"/><br/>
                <input type="text" placeholder="ImgURL" onChange={handleImgURL} value = {imgURL} name = "image"/> <br/>
                <input type="text" placeholder="Price" onChange={handlePrice} value = {price} name = "price"/><br/>
                <input type="text" placeholder="Description" onChange={handleDetails} details = {details} name = "details"/><br/>

                <input type="submit" value="Publish Shoe"/>
            </form>

            <Grid item  xs={12} sm={6} md={4} lg={3} >

            <div>
            <Card className={classes.root}>

                <CardMedia className={classes.media} image={src} title={name} />

                <CardContent>
                    <div className={classes.cardContent}>
                        <Typography variant="h5" gutterBottom>
                            {name}
                        </Typography>
                        <Typography variant="h6">
                            {price}
                        </Typography>
                    </div>
                    <Typography variant="overline" color="textSecondary">
                        {details}
                    </Typography>
                </CardContent>

                <CardActions disableSpacing className={classes.cardActions}>
                    <FavoriteBorder />  
                    <IconButton aria-label="Add to Cart" >
                        <AddShoppingCart />
                    </IconButton>
                    <Typography variant="overline" color="textSecondary">
                     Favorites
                    </Typography>
                </CardActions>
            </Card>      
            </div>
            </Grid>
        </div>
    )

}

export default ProductForm;
