import { useState } from 'react'
import React from 'react';
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton, Grid } from '@material-ui/core';
import useStyles from './ProductFormStyles';
import { AddShoppingCart, Favorite, FavoriteBorder } from "@material-ui/icons";



function ProductForm() {
    const classes = useStyles()

    const src = 'https://static.nike.com/a/images/t_PDP_1728_v1/f_auto,b_rgb:f5f5f5/2d9af5a0-de12-4fc1-ac5c-43a085a23cb1/react-vision-womens-shoes-kGhsR6.png'


    const [name, setName] = useState("Placeholder")
    const [imgURL, setImgURL] = useState(src)
    const [price, setPrice] = useState("$")
    const [details, setDetails] = useState("")


    function handleName(e){
        console.log(e.target.value)
        setName(e.target.value)
    }

    function handleImgURL(e){
        console.log(e.target.value)
        setImgURL(e.target.value)
    }

    function handlePrice(e){
        console.log(e.target.value)

        setPrice(`$${e.target.value}`)
    }

    function handleDetails(e){
        console.log(e.target.value)
        setDetails(e.target.value)
    }


    return (
        <div>
            <h1> Become a seller?</h1>
            <form>
            <input type="text" placeholder="Shoe Name" onChange={handleName}/><br/>
                <input type="text" placeholder="ImgURL" onChange={handleImgURL}/> <br/>
                <input type="text" placeholder="price" onChange={handlePrice}/><br/>
                <input type="text" placeholder="details" onChange={handleDetails}/><br/>

                <input type="submit" value="Publish"/>
            </form>

            {/* <Typography>
                    <strong>{name}</strong>
            </Typography>

            <Typography>
            <img src={imgURL}  height="40px"  />
            </Typography>

            <Typography>
                    <strong>{price}</strong>
            </Typography>

            <Typography>
                    <strong>{details}</strong>
            </Typography> */}
            <Grid item  xs={12} sm={6} md={4} lg={3} >

            <div>
            <Card className={classes.root}>

                <CardMedia className={classes.media} image={imgURL} title={name} />

                <CardContent>
                    <div className={classes.cardContent}>
                        <Typography variant="h5" gutterBottom>
                            {name}
                        </Typography>
                        <Typography variant="h6">
                            {price}
                        </Typography>
                    </div>
                    <Typography variant="overline" color="textSecondary">
                        {details}
                    </Typography>
                </CardContent>

                <CardActions disableSpacing className={classes.cardActions}>
                    <FavoriteBorder />  
                    <IconButton aria-label="Add to Cart" >
                        <AddShoppingCart />
                    </IconButton>
                    <Typography variant="overline" color="textSecondary">
                    3000 Favorites
                    </Typography>
                </CardActions>
            </Card>


            </div>
            </Grid>
{/* 
            <Grid container justifyContent="center" spacing={5}>
                {displayProducts}
            </Grid> */}


        </div>
    )

}

export default ProductForm;
