import { useState } from 'react'
import React from 'react';
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton, Grid, TextField } from '@material-ui/core';
import useStyles from './ProductFormStyles';
import { AddShoppingCart, Favorite, FavoriteBorder } from "@material-ui/icons";
import placeholder from "../../Assets/placeholder.jpg"




function ProductForm() {
    const classes = useStyles()

    const src = placeholder

    // const [name, setName] = useState("")
    // const [imgURL, setImgURL] = useState("")
    // const [price, setPrice] = useState("")
    // const [details, setDetails] = useState("")

    const [submittedData, setSubmittedData] = useState([])

    const [formData, setFormData] = useState({
        name: "",
        image: `${src}`,
        quantity: 0,
        price: "",
        description: "SKU: 100",
        details: "",
        likes : 0
    })

    function handleFormData(e){
        const name = e.target.name
        const value = e.target.value

        setFormData({
            ...formData,
            [name] : value
        })
    }


    // function handleName(e){
    //     console.log(e.target.value)
    //     setName(e.target.value)
    // }

    // function handleImgURL(e){
    //     console.log(e.target.value)
    //     setImgURL(e.target.value)
    // }

    // function handlePrice(e){
    //     console.log(e.target.value)

    //     setPrice(`$${e.target.value}`)
    // }

    // function handleDetails(e){
    //     console.log(e.target.value)
    //     setDetails(e.target.value)
    // }

    function handleSubmit(e){
        e.preventDefault()

        console.log(e.target)

        const formDataSubmit = {
        name: formData.name,
        image: formData.image,
        quantity: 0,
        price: formData.price,
        description: "SKU: 100",
        details: formData.details,
        likes : 0
        }

        const dataArray = [...submittedData, formDataSubmit]


        console.log(formDataSubmit)

        setSubmittedData(dataArray)

        setFormData({
            name: "",
            image: `${src}`,
            quantity: 0,
            price: "",
            description: "SKU: 100",
            details: "",
            likes : 0
        })
    }


    return (
        <div>
            <h1> Become a seller?</h1>
            <form onSubmit={handleSubmit}> 
            <Grid>
                <TextField variant = "outlined" size ="small" defaultValue="Shoe Name" margin ="dense"/><br/>
                <TextField variant = "outlined" size ="small" defaultValue="Shoe Name" margin = "dense"/><br/>
                <TextField variant = "outlined" size ="small" defaultValue="Shoe Name" margin = "small"/><br/>
                <TextField variant = "outlined" size ="small" defaultValue="Shoe Name" />

                <input type="text" placeholder="Shoe Name" onChange={handleFormData} value = {formData.name} name = "name"/><br/>
                <input type="text" placeholder="ImgURL" onChange={handleFormData}  name = "image"/> <br/>
                <input type="text" placeholder="Price" onChange={handleFormData} value = {formData.price} name = "price"/><br/>
                <input type="text" placeholder="Description" onChange={handleFormData} details = {formData.details} name = "details"/><br/>
            </Grid>

            <Grid item  xs={12} sm={6} md={4} lg={3} >

            <div>
            <Card className={classes.root}>

                <CardMedia className={classes.media} image={formData.image} title={formData.name} />

                <CardContent>
                    <div className={classes.cardContent}>
                        <Typography variant="h5" gutterBottom>
                            {formData.name}
                        </Typography>
                        <Typography variant="h6">
                            ${formData.price}
                        </Typography>
                    </div>
                    <Typography variant="overline" color="textSecondary">
                        {formData.details}
                    </Typography>
                </CardContent>

                <CardActions disableSpacing className={classes.cardActions}>
                    <FavoriteBorder />  
                    <IconButton aria-label="Add to Cart" >
                        <AddShoppingCart />
                    </IconButton>
                    <Typography variant="overline" color="textSecondary">
                     Favorites
                    </Typography>
                </CardActions>
            </Card>      
            </div>
            </Grid>

                <input type="submit" value="Publish Shoe"/>
            </form>
        </div>
    )

}

export default ProductForm;
