import { useState } from 'react'
import React from 'react';
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton, Grid } from '@material-ui/core';
import useStyles from './ProductFormStyles';



function ProductForm() {
    const classes = useStyles()

    const [name, setName] = useState("Shoe Name")
    const [imgURL, setImgURL] = useState("https://cloud.camper.com/is/image/JGVzaG9wMDNuYmlnZ3JleSQ=/B2147-044_F.jpg")
    const [price, setPrice] = useState("Shoe Price")
    const [details, setDetails] = useState("Shoe Description")


    function handleName(e){
        console.log(e.target.value)
        setName(e.target.value)
    }

    function handleImgURL(e){
        console.log(e.target.value)
        setImgURL(e.target.value)
    }

    function handlePrice(e){
        console.log(e.target.value)

        setPrice(`$${e.target.value}`)
    }

    function handleDetails(e){
        console.log(e.target.value)
        setDetails(e.target.value)
    }


    return (
        <div>
            <h1> Become a seller?</h1>
            <form>
            <input type="text" placeholder="Shoe Name" onChange={handleName}/><br/>
                <input type="text" placeholder="ImgURL" onChange={handleImgURL}/> <br/>
                <input type="text" placeholder="price" onChange={handlePrice}/><br/>
                <input type="text" placeholder="details" onChange={handleDetails}/><br/>

                <input type="submit" value="Publish"/>
            </form>

            {/* <Typography>
                    <strong>{name}</strong>
            </Typography>

            <Typography>
            <img src={imgURL}  height="40px"  />
            </Typography>

            <Typography>
                    <strong>{price}</strong>
            </Typography>

            <Typography>
                    <strong>{details}</strong>
            </Typography> */}
            <Grid item  xs={12} sm={6} md={4} lg={3} >

            <div>
            <Card className={classes.root}>

                <CardMedia className={classes.media} image={imgURL} title={name} />

                <CardContent>
                    <div className={classes.cardContent}>
                        <Typography variant="h5" gutterBottom>
                            {name}
                        </Typography>
                        <Typography variant="h6">
                            {price}
                        </Typography>
                    </div>
                    <Typography variant="overline" color="textSecondary">
                        {details}
                    </Typography>
                </CardContent>
            </Card>
            </div>
            </Grid>

            <Grid container justifyContent="center" spacing={5}>
                {displayProducts}
            </Grid>


        </div>
    )

}

export default ProductForm;
